bool cmp(f A, f B) { return A.d &lt; B.d; }

// 小根堆维护最小值
priority_queue&lt;long long, vector&lt;long long&gt;, greater&lt;long long&gt;&gt; q;

int main() {
  long long n, i;
  cin &gt;&gt; n;
  for (i = 1; i &lt;= n; i++) {
    cin &gt;&gt; a[i].d &gt;&gt; a[i].p;
  }
  sort(a + 1, a + n + 1, cmp);
  long long ans = 0;
  for (i = 1; i &lt;= n; i++) {
    if (a[i].d &lt;= (int)q.size()) {  // 超过截止时间
      if (q.top() &lt; a[i].p) {       // 后悔
        ans += a[i].p - q.top();
        q.pop();
        q.push(a[i].p);
      }
    } else {  // 直接加入队列
      ans += a[i].p;
      q.push(a[i].p);
    }
  }
