void shell_sort(T array[], int length) {
  int h = 1;
  while (h &lt; length / 3) {
    h = 3 * h + 1;
  }
  while (h &gt;= 1) {
    for (int i = h; i &lt; length; i++) {
      for (int j = i; j &gt;= h &amp;&amp; array[j] &lt; array[j - h]; j -= h) {
        std::swap(array[j], array[j - h]);
      }
    }
    h = h / 3;
  }
}
