  int state[MaxSZ];
  long long key[MaxSZ];

  void clear() {
    sz = 0;
    memset(head, -1, sizeof(head));
  }

  void push(int s) {
    int x = s % Prime;
    for (int i = head[x]; ~i; i = next[i]) {
      if (state[i] == s) {
        key[i] += d;
        return;
      }
    }
    state[sz] = s, key[sz] = d;
    next[sz] = head[x];
    head[x] = sz++;
  }

  void roll() {
    for (int i = 0; i &lt; sz; i++) state[i] &lt;&lt;= offset;
  }
} H[2], *H0, *H1;

int b[M + 1], bb[M + 1];

int encode() {
  int s = 0;
  memset(bb, -1, sizeof(bb));
  int bn = 1;
  bb[0] = 0;
  for (int i = m; i &gt;= 0; --i) {
    if (!~bb[b[i]]) bb[b[i]] = bn++;
    s &lt;&lt;= offset;
    s |= bb[b[i]];
  }
  return s;
}

void decode(int s) {
  for (int i = 0; i &lt; m + 1; i++) {
    b[i] = s &amp; mask;
    s &gt;&gt;= offset;
  }
}

void push(int j, int dn, int rt) {
  b[j] = dn;
  b[j + 1] = rt;
  H1-&gt;push(encode());
}

int main() {
  cin &gt;&gt; n &gt;&gt; m;
  if (m &gt; n) swap(n, m);
  H0 = H, H1 = H + 1;
  H1-&gt;clear();
  d = 1;
  H1-&gt;push(0);
  for (int i = 0; i &lt; n; i++) {
    for (int j = 0; j &lt; m; j++) {
      swap(H0, H1);
      H1-&gt;clear();
      for (int ii = 0; ii &lt; (H0-&gt;sz); ii++) {
        decode(H0-&gt;state[ii]);
        d = H0-&gt;key[ii];
        int lt = b[j], up = b[j + 1];
        bool dn = i != n - 1, rt = j != m - 1;
        if (lt &amp;&amp; up) {
          if (lt == up) {
            if (i == n - 1 &amp;&amp; j == m - 1) {
              push(j, 0, 0);
            }
          } else {
            for (int i = 0; i &lt; m + 1; i++)
              if (b[i] == lt) b[i] = up;
            push(j, 0, 0);
          }
        } else if (lt || up) {
          int t = lt | up;
          if (dn) {
            push(j, t, 0);
          }
          if (rt) {
            push(j, 0, t);
          }
        } else {
          if (dn &amp;&amp; rt) {
            push(j, m, m);
          }
        }
      }
    }
    H1-&gt;roll();
  }
  assert(H1-&gt;sz &lt;= 1);
  cout &lt;&lt; (H1-&gt;sz == 1 ? H1-&gt;key[0] : 0) &lt;&lt; endl;
}
