  b[j + 1] = rt;
  H1[c].push(encode());
}

void init() {
  cin &gt;&gt; n &gt;&gt; m;
  H0 = H[0], H1 = H[1];
  for (int c = 0; c &lt; 3; c++) H1[c].clear();
  d = 0;
  H1[0].push(0);
  memset(A, 0, sizeof(A));
  for (int i = 0; i &lt; n; i++)
    for (int j = 0; j &lt; m; j++) cin &gt;&gt; A[i][j];
}

void solve() {
  ans = 0;
  for (int i = 0; i &lt; n; i++) {
    for (int j = 0; j &lt; m; j++) {
      checkMax(ans, A[i][j]);  // 需要单独处理一个格子的情况
      if (!A[i][j]) continue;  // 如果有障碍，则跳过，注意这时状态数组不需要滚动
