参考实现 <pre><code>#include &lt;cstdio&gt;
#include &lt;cstring&gt;
using namespace std;

constexpr int MAXN = 1e3 + 10;

double a[MAXN][MAXN], f[MAXN];
int n, m;

void solve(int x) {
  memset(a, 0, sizeof a);
  for (int i = 1; i &lt;= m; i++) {
    if (i == 1) {
      a[i][i] = 2;
      a[i][i + 1] = -1;
      a[i][m + 1] = 3 + f[i];
      continue;
    } else if (i == m) {
      a[i][i] = 2;
      a[i][i - 1] = -1;
      a[i][m + 1] = 3 + f[i];
      continue;
    }
    a[i][i] = 3;
    a[i][i + 1] = -1;
    a[i][i - 1] = -1;
    a[i][m + 1] = 4 + f[i];
  }

  for (int i = 1; i &lt; m; i++) {
    double p = a[i + 1][i] / a[i][i];
    a[i + 1][i] = 0;
    a[i + 1][i + 1] -= a[i][i + 1] * p;
    a[i + 1][m + 1] -= a[i][m + 1] * p;
  }

  f[m] = a[m][m + 1] / a[m][m];
  for (int i = m - 1; i &gt;= 1; i--)
    f[i] = (a[i][m + 1] - f[i + 1] * a[i][i + 1]) / a[i][i];
}

int main() {
  scanf("%d %d", &amp;n, &amp;m);
  int st, ed;
  scanf("%d %d", &amp;st, &amp;ed);
  if (m == 1) {
    printf("%.10f\n", 2.0 * (n - st));
    return 0;
  }
  for (int i = n - 1; i &gt;= st; i--) {
    solve(i);
  }
  printf("%.10f\n", f[ed]);
  return 0;
}
</code></pre>

参考代码 <pre><code>#include &lt;algorithm&gt;
#include &lt;iostream&gt;
using namespace std;
long long sta[2005], sit[2005], f[15][2005][105];
int n, k, cnt;

void dfs(int x, int num, int cur) {
  if (cur &gt;= n) {  // 有新的合法状态
    sit[++cnt] = x;
    sta[cnt] = num;
    return;
  }
  dfs(x, num, cur + 1);  // cur位置不放国王
  dfs(x + (1 &lt;&lt; cur), num + 1,
      cur + 2);  // cur位置放国王，与它相邻的位置不能再放国王
}

bool compatible(int j, int x) {
  if (sit[j] &amp; sit[x]) return false;
  if ((sit[j] &lt;&lt; 1) &amp; sit[x]) return false;
  if (sit[j] &amp; (sit[x] &lt;&lt; 1)) return false;
  return true;
}

int main() {
  cin &gt;&gt; n &gt;&gt; k;
  dfs(0, 0, 0);  // 先预处理一行的所有合法状态
  for (int j = 1; j &lt;= cnt; j++) f[1][j][sta[j]] = 1;
  for (int i = 2; i &lt;= n; i++)
    for (int j = 1; j &lt;= cnt; j++)
      for (int x = 1; x &lt;= cnt; x++) {
        if (!compatible(j, x)) continue;  // 排除不合法转移
        for (int l = sta[j]; l &lt;= k; l++) f[i][j][l] += f[i - 1][x][l - sta[j]];
      }
  long long ans = 0;
  for (int i = 1; i &lt;= cnt; i++) ans += f[n][i][k];  // 累加答案
  cout &lt;&lt; ans &lt;&lt; endl;
  return 0;
}
</code></pre>

