pub fn build_delta_2_table_improved_minghu6(p: &amp;[impl PartialEq]) -&gt; Vec&lt;usize&gt; {
    let patlen = p.len();
    let lastpos = patlen - 1;
    let mut delta_2 = Vec::with_capacity(patlen);

    // 第一种情况
    // delta_2[j] = lastpos * 2 - j
    for i in 0..patlen {
        delta_2.push(lastpos * 2 - i);
    }

    // 第二种情况
